## vue axios

axiosgetpostputpatchdelete

* get
* post+
* put
* patch
* delete

```js
//axiosget
axios.get('/data.json').then((res) => {
  console.log(res)
});

//axiosget
axios.get('/data.json', {
  params: {
    id: 12
  }
}).then((res) => {
  console.log(res)
});

//axiosget
axios({
  method: 'get',
  url: '/data.json',
}).then(res => {
  console.log(res)
});

//axiosget
axios({
  method: 'get',
  url: '/data.json',
  params: {
    id: 12
  },
}).then(res => {
  console.log(res)
});

//axiospost
let data = {
  id: 12
};
axios.post('/post', data).then((res) => {
  console.log(res)
});

//axiospost
axios({
  method: 'post',
  url: '/post',
  data: data
}).then(res => {
  console.log(res)
});

//form-data,multipart/form-dataapplication/json

let formData = new formData()
for (let key in data) {
  formData.append(key, data[key])
};
axios.post('/post', formData).then(res => {
  console.log(res)
});

//axiosput
axios.put('/put', data).then(res => {
  console.log(res)
})

//axiospatch
axios.patch('/patch', data).then(res => {
  console.log(res)
});

//axiosdelete
axios.delete('/delete', {
  params: {
    id: 12
  }
}).then(res => {
  console.log(res)
})
//:paramsURL
// http://localhost:8080/delete?id=12,data

//axiosdelete
axios.delete('/delete', {
  data: {
    id: 12
  }
}).then(res => {
  console.log(res)
})
