function diffProps(oldProps, newProps) {
  let propsPathchs = []
  // 
  // 
  // 
  // 
  forin(olaProps, (k, v) => {
    if (!newProps.hasOwnProperty(k)) {
      propsPathchs.push({
        type: 'remove',
        prop: k
      })
    } else {
      if (v !== newProps[k]) {
        propsPathchs.push({
          type: 'change',
          prop: k,
          value: newProps[k]
        })
      }
    }
  })
  forin(newProps, (k, v) => {
    if (!oldProps.hasOwnProperty(k)) {
      propsPathchs.push({
        type: 'add',
        prop: k,
        value: v
      })
    }
  })
  return propsPathchs
}

// 
function diffChildrens(oldChild, newChild, index, pathchs) {
  // //
  let {
    change,
    list
  } = diffList(oldChild, newChild, index, pathchs)
  if (change.length) {
    if (pathchs[index]) {
      pathchs[index] = pathchs[index].concat(change)
    } else {
      pathchs[index] = change
    }
  }

  //  key 
  oldChild.map((item, i) => {
    let keyIndex = list.indexOf(item.key)
    if (keyIndex) {
      let node = newChild[keyIndex]
      // 
      dfs(item, node, index, pathchs)
    }
  })
}

//  key 
// //
function diffList(oldList, newList, index, pathchs) {
  let change = []
  let list = []
  const newKeys = getKey(newList)
  oldList.map(v => {
    if (newKeys.indexOf(v.key) > -1) {
      list.push(v.key)
    } else {
      list.push(null)
    }
  })

  // 
  for (let i = list.length - 1; i >= 0; i--) {
    if (!list[i]) {
      list.splice(i, 1)
      change.push({
        type: 'remove',
        index: i
      })
    }
  }

  // 
  newList.map((item, i) => {
    const key = item.key
    const index = list.indexOf(key)
    if (index === -1 || key == null) {
      // 
      change.push({
        type: 'add',
        node: item,
        index: i
      })
      list.splice(i, 0, key)
    } else {
      // 
      if (index !== i) {
        change.push({
          type: 'move',
          form: index,
          to: i,
        })
        move(list, index, i)
      }
    }
  })

  return {
    change,
    list
  }
}
