giteepages
```
// npm >= 10.18
const puppeteer = require('puppeteer');
const username = 'luckyship';                                            // 
const giteePageUrl = 'https://gitee.com/luckyship/luckyship/pages';      // gitee page

async function giteeUpdate(username, giteePageUrl, passwd) {
    const browser = await puppeteer.launch({
        //  false , 
          headless: false,
    });
    const page = await browser.newPage();
    await page.goto('https://gitee.com/login');
    // 1. 
    let accountElements = await page.$x('//*[@id="user_login"]') //  xpath 
    // 2. 
    await accountElements[0].type(username)
    // 3. 
    let pwdElements = await page.$x('//*[@id="user_password"]')
    // 4. 
    await pwdElements[0].type(passwd)
    // 5. 
    let loginButtons = await page.$x('//*[@id="new_user"]/div[2]/div/div/div[4]/input')
    await loginButtons[0].click()
    // 6. 
    await page.waitFor(1000)
    await page.goto(giteePageUrl); //  https://gitee.com/yang0033/hexo-blog/pages
    // 7.1.  7 
    await page.on('dialog', async dialog => {
        console.log('')
        dialog.accept();
    })
    // 7. 
    let updateButtons = await page.$x('//*[@id="pages-branch"]/div[7]')
    await updateButtons[0].click()
    // 8. 
    while (true) {
        await page.waitFor(2000)
        try {
            // 8.1 
            deploying = await page.$x('//*[@id="pages_deploying"]')
            if (deploying.length > 0) {
                console.log('...')
            } else {
                console.log('')
                break;
            }
        } catch (error) {
            break;
        }
    }
    await page.waitFor(500);
    // 10.
    browser.close();
}

process.stdin.resume();
process.stdin.setEncoding('utf-8');
process.stdout.write(`${username}:`);
process.stdin.on('data', function (data) {
    var str = data.slice(0, -2);
    process.stdin.emit('end');

    giteeUpdate(username, giteePageUrl, str);
});
process.stdin.on('end', function () {
     process.stdin.pause();
});

```

